import ProductEditScreen from './screens/ProductEditScreen';
import ProductCreateScreen from './screens/ProductCreateScreen';
import UserEditScreen from './screens/UserEditScreen';
import ProfileScreen from './screens/ProfileScreen';
import SearchScreen from './screens/SearchScreen';
import SigninScreen from './screens/SigninScreen';
import SignupScreen from './screens/SignupScreen';
import UserListScreen from './screens/UserListScreen';
import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import QualityInfo from './components/QualityInfo';
import AboutScreen from './screens/AboutScreen';
import ControlledCarousel from './components/ControlledCarousel';
import { Card } from 'react-bootstrap';
import MenuScreen from './screens/MenuScreen';
import ShopScreen from './screens/ShopScreen';

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { fullBox, cart, userInfo } = state;

  const signoutHandler = () => {
    ctxDispatch({ type: 'USER_SIGNOUT' });
    localStorage.removeItem('userInfo');
    localStorage.removeItem('shippingAddress');
    localStorage.removeItem('paymentMethod');
    window.location.href = '/signin';
  };
  const [sidebarIsOpen, setSidebarIsOpen] = useState(false);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const updateOrderStatus = async () => {
      try {
        const instance = axios.create({ baseURL: 'http://localhost:5000' });
        const { data } = await instance.get('/api/orders/updateOrder', {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        console.log('data', data);
      } catch (err) {
        console.log(err);
      }
    };
    // Check to see if this is a redirect back from Checkout
    const query = new URLSearchParams(window.location.search);
    if (query.get('success')) {
      console.log('Order placed! You will receive an email confirmation.');
      ctxDispatch({
        type: 'CART_CLEAR',
      });
      localStorage.removeItem('cartItems');
      updateOrderStatus();
    }
    if (query.get('cancelled')) {
      console.log(
        "Order cancelled -- continue to shop around and checkout when you're ready."
      );
    }
    const fetchCategories = async () => {
      try {
        const instance = axios.create({ baseURL: 'http://localhost:5000' });
        const { data } = await instance.get(`/api/products/categories`);
        setCategories(data);
      } catch (err) {
        toast.error(getError(err));
      }
    };
    fetchCategories();
  }, []);

  return (
    <BrowserRouter>
      <div
        className={
          sidebarIsOpen
            ? fullBox
              ? 'site-container active-cont d-flex flex-column full-box'
              : 'site-container active-cont d-flex flex-column'
            : fullBox
            ? 'site-container d-flex flex-column full-box'
            : 'site-container d-flex flex-column'
        }
      >
        {/* <ToastContainer position="bottom-center" limit={1} /> */}
        <header>
          <Navbar bg="dark" variant="dark" expand="sm" fixed="top">
            <Container>
              <Button
                variant="dark"
                onClick={() => setSidebarIsOpen(!sidebarIsOpen)}
              >
                <i className="fas fa-bars"></i>
              </Button>

              <LinkContainer to="/">
                <Navbar.Brand>Caffeine Confessions</Navbar.Brand>
              </LinkContainer>
              <Navbar.Toggle aria-controls="basic-navbar-nav" />
              <Navbar.Collapse id="basic-navbar-nav">
                {/* <SearchBox /> */}
                <Nav
                  className="me-auto w-100 justify-content-end my-lg-4"
                  as="ul"
                >
                  <Link to="/" className="nav-link">
                    Home
                  </Link>
                  {/* <Nav.Link xs lg="2" href="/">
                    Home
                  </Nav.Link> */}
                  <Link to="/menu" className="nav-link">
                    Menu
                  </Link>
                  {/* <Nav.Link href="/menu">Menu</Nav.Link> */}
                  <Link to="/about" className="nav-link">
                    About
                  </Link>
                  {/* <Nav.Link href="/about">About</Nav.Link> */}
                  <Link to="/shop" className="nav-link">
                    Shop
                  </Link>
                  {/* <Nav.Link href="/shop">Shop</Nav.Link> */}
                  <Link to="/cart" className="nav-link">
                    Cart
                    {cart.cartItems.length > 0 && cart.cartItems[0] !== null && (
                      <Badge pill bg="danger">
                        {cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
                      </Badge>
                    )}
                  </Link>
                  {userInfo ? (
                    <NavDropdown title={userInfo.name} id="basic-nav-dropdown">
                      <LinkContainer to="/profile">
                        <NavDropdown.Item>User Profile</NavDropdown.Item>
                      </LinkContainer>
                      <LinkContainer to="/orderhistory">
                        <NavDropdown.Item>Order History</NavDropdown.Item>
                      </LinkContainer>
                      <NavDropdown.Divider />
                      <Link
                        className="dropdown-item"
                        to="#signout"
                        onClick={signoutHandler}
                      >
                        Sign Out
                      </Link>
                    </NavDropdown>
                  ) : (
                    <Link className="nav-link" to="/signin">
                      Sign In
                    </Link>
                  )}
                  {userInfo && userInfo.isAdmin && (
                    <NavDropdown title="Admin" id="admin-nav-dropdown">
                      <LinkContainer to="/admin/dashboard">
                        <NavDropdown.Item>Dashboard</NavDropdown.Item>
                      </LinkContainer>
                      <LinkContainer to="/admin/products">
                        <NavDropdown.Item>Products</NavDropdown.Item>
                      </LinkContainer>
                      <LinkContainer to="/admin/orders">
                        <NavDropdown.Item>Orders</NavDropdown.Item>
                      </LinkContainer>
                      <LinkContainer to="/admin/users">
                        <NavDropdown.Item>Users</NavDropdown.Item>
                      </LinkContainer>
                    </NavDropdown>
                  )}
                </Nav>
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </header>
        {/* <div
          className={
            sidebarIsOpen
              ? 'active-nav side-navbar d-flex justify-content-between flex-wrap flex-column'
              : 'side-navbar d-flex justify-content-between flex-wrap flex-column'
          }
        >
          <Nav className="flex-column text-white w-100 p-2">
            <Nav.Item>
              <strong>Categories</strong>
            </Nav.Item>
            {categories.map((category) => (
              <Nav.Item key={category}>
                <LinkContainer
                  to={`/search?category=${category}`}
                  onClick={() => setSidebarIsOpen(false)}
                >
                  <Nav.Link>{category}</Nav.Link>
                </LinkContainer>
              </Nav.Item>
            ))}
          </Nav>
        </div> */}
        <main
          style={{
            backgroundImage: 'url(' + '/images/bg_4.jpg' + ')',
            height: '100%',
            width: '100%',
          }}
        >
          <ControlledCarousel />
          {/* <Container className="mt-3"> */}
          <Routes>
            <Route path="/product/:slug" element={<ProductScreen />} />
            <Route path="/cart" element={<CartScreen />} />
            <Route path="/search" element={<SearchScreen />} />
            <Route path="/menu" element={<MenuScreen />} />
            <Route path="/shop" element={<ShopScreen />} />
            <Route path="/signin" element={<SigninScreen />} />
            <Route path="/signup" element={<SignupScreen />} />
            <Route path="/about" element={<AboutScreen />}></Route>
            <Route
              path="/profile"
              element={
                <ProtectedRoute>
                  <ProfileScreen />
                </ProtectedRoute>
              }
            />
            {/* <Route
                path="/map"
                element={
                  <ProtectedRoute>
                    <MapScreen />
                  </ProtectedRoute>
                }
              /> */}
            {/* <Route path="/placeorder" element={<PlaceOrderScreen />} /> */}
            {/* <Route
                path="/order/:id"
                element={
                  <ProtectedRoute>
                    <OrderScreen />
                  </ProtectedRoute>
                }
              ></Route> */}
            <Route
              path="/orderhistory"
              element={
                <ProtectedRoute>
                  <OrderHistoryScreen />
                </ProtectedRoute>
              }
            ></Route>
            {/* <Route
                path="/shipping"
                element={<ShippingAddressScreen />}
              ></Route> */}
            {/* <Route path="/payment" element={<PaymentMethodScreen />}></Route> */}
            {/* Admin Routes */}
            <Route
              path="/admin/dashboard"
              element={
                <AdminRoute>
                  <DashboardScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/orders"
              element={
                <AdminRoute>
                  <OrderListScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/users"
              element={
                <AdminRoute>
                  <UserListScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/products"
              element={
                <AdminRoute>
                  <ProductListScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/product/:id"
              element={
                <AdminRoute>
                  <ProductEditScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/product/create"
              element={
                <AdminRoute>
                  <ProductCreateScreen />
                </AdminRoute>
              }
            ></Route>
            <Route
              path="/admin/user/:id"
              element={
                <AdminRoute>
                  <UserEditScreen />
                </AdminRoute>
              }
            ></Route>
            <Route path="/" element={<HomeScreen />} />
          </Routes>
          {/* </Container> */}
        </main>
        <footer style={{ background: '#120f0f' }}>
          <div className="text-center">All rights reserved</div>
        </footer>
      </div>
    </BrowserRouter>
  );
}

